import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";

const App = () => {
  const [url, setUrl] = useState("");
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [serverUrl, setServerUrl] = useState("");
  const [canais, setCanais] = useState([]);
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progresso, setProgresso] = useState(0);
  const [pesquisa, setPesquisa] = useState("");
  const videoRef = useRef(null);

  // Função para carregar a programação de um canal
  const carregarProgramacao = async (canalUrl) => {
    try {
      const { data } = await axios.get(`${canalUrl}/epg`); // Exemplo de endpoint EPG
      return data;
    } catch (error) {
      console.error("Erro ao carregar programação:", error);
      return [];
    }
  };

  // Outras funções do código permanecem iguais...

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* ... Sidebar e inputs */}

      <div style={{ width: "70%", padding: "20px" }}>
        {selecionado ? (
          <div>
            <h3>{selecionado.nome}</h3>
            <video ref={videoRef} controls style={{ width: "100%", height: "500px" }} />
            <div>
              <h4>Programação</h4>
              <ul>
                {selecionado.programacao?.map((item, idx) => (
                  <li key={idx}>{item.horario} - {item.titulo}</li>
                )) || <p>Sem programação disponível.</p>}
              </ul>
            </div>
          </div>
        ) : (
          <p>Selecione um canal para assistir.</p>
        )}
      </div>
    </div>
  );
};

export default App;
