import React, { useState, useRef } from "react";
import axios from "axios";

const App = () => {
  const [modo, setModo] = useState("painel"); // Alternar entre "painel" e "m3u"
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [urlServidor, setUrlServidor] = useState("");
  const [canais, setCanais] = useState({});
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progresso, setProgresso] = useState(0); // Controle do progresso de carregamento
  const videoRef = useRef(null);

  // Função para processar a lista M3U
  const processarM3U = (data) => {
    const linhas = data.split("\n");
    const lista = [];
    let categoriaAtual = "Outros";

    for (let i = 0; i < linhas.length; i++) {
      if (linhas[i].startsWith("#EXTINF")) {
        const nome = linhas[i].split(",")[1];
        const urlStream = linhas[i + 1];
        if (linhas[i].includes("group-title")) {
          const match = linhas[i].match(/group-title="([^"]+)"/);
          if (match) categoriaAtual = match[1];
        }
        lista.push({ nome, urlStream, categoria: categoriaAtual });
      }
      setProgresso(Math.round((i / linhas.length) * 100)); // Atualiza a porcentagem
    }

    // Organiza os canais por categoria
    const canaisPorCategoria = lista.reduce((acc, canal) => {
      if (!acc[canal.categoria]) acc[canal.categoria] = [];
      acc[canal.categoria].push(canal);
      return acc;
    }, {});

    setCanais(canaisPorCategoria);
    setLoading(false);
    setProgresso(100);
  };

  // Função para carregar canais via login/senha
  const carregarCanais = async () => {
    setLoading(true);
    setProgresso(0);

    try {
      const urlM3U = `${urlServidor}/get.php?username=${login}&password=${senha}&type=m3u_plus&output=mpegts`;
      const { data } = await axios.get(urlM3U);
      processarM3U(data);
    } catch (error) {
      console.error("Erro ao carregar canais:", error);
      setLoading(false);
    }
  };

  // Função para carregar arquivo M3U local
  const carregarArquivo = (e) => {
    const arquivo = e.target.files[0];
    if (arquivo) {
      setLoading(true);
      setProgresso(0);
      const leitor = new FileReader();
      leitor.onload = (event) => processarM3U(event.target.result);
      leitor.readAsText(arquivo);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>IPTV Web Player</h1>

      {/* Escolha do Modo */}
      <div>
        <label>
          <input
            type="radio"
            value="painel"
            checked={modo === "painel"}
            onChange={() => setModo("painel")}
          />
          Usar Login/Senha do Painel IPTV
        </label>
        <label style={{ marginLeft: "20px" }}>
          <input
            type="radio"
            value="m3u"
            checked={modo === "m3u"}
            onChange={() => setModo("m3u")}
          />
          Carregar Arquivo M3U
        </label>
      </div>

      {/* Opção de Login e Senha */}
      {modo === "painel" && (
        <div>
          <input
            type="text"
            placeholder="Login"
            value={login}
            onChange={(e) => setLogin(e.target.value)}
            style={{ margin: "5px", padding: "8px" }}
          />
          <input
            type="password"
            placeholder="Senha"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            style={{ margin: "5px", padding: "8px" }}
          />
          <input
            type="text"
            placeholder="URL do Servidor IPTV"
            value={urlServidor}
            onChange={(e) => setUrlServidor(e.target.value)}
            style={{ margin: "5px", padding: "8px", width: "300px" }}
          />
          <button onClick={carregarCanais} style={{ marginLeft: "10px" }}>
            Carregar Canais
          </button>
        </div>
      )}

      {/* Opção de Carregar Arquivo M3U */}
      {modo === "m3u" && (
        <div>
          <input type="file" accept=".m3u" onChange={carregarArquivo} />
        </div>
      )}

      {/* Loader com Porcentagem */}
      {loading && (
        <div style={{ marginTop: "10px" }}>
          <p>Carregando canais... {progresso}%</p>
          <div
            style={{
              width: "100%",
              height: "10px",
              background: "#ddd",
              borderRadius: "5px",
            }}
          >
            <div
              style={{
                width: `${progresso}%`,
                height: "100%",
                background: "#007bff",
                borderRadius: "5px",
              }}
            ></div>
          </div>
        </div>
      )}

      {/* Lista de Canais e Player */}
      <div style={{ display: "flex", marginTop: "20px" }}>
        <div
          style={{
            width: "30%",
            borderRight: "1px solid #ddd",
            overflowY: "scroll",
            maxHeight: "600px",
          }}
        >
          <h3>Lista de Canais</h3>
          {Object.keys(canais).map((categoria) => (
            <div key={categoria}>
              <h4>{categoria}</h4>
              <ul>
                {canais[categoria].map((canal, index) => (
                  <li key={index}>
                    <button
                      onClick={() => setSelecionado(canal)}
                      style={{
                        display: "block",
                        width: "100%",
                        textAlign: "left",
                        padding: "5px",
                        background: "none",
                        border: "none",
                        cursor: "pointer",
                      }}
                    >
                      {canal.nome}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>

        {/* Player de Vídeo */}
        <div style={{ width: "70%", padding: "20px", overflow: "hidden" }}>
          {selecionado ? (
            <div>
              <h3>{selecionado.nome}</h3>
              <video
                ref={videoRef}
                controls
                style={{ width: "100%", height: "500px" }}
                onError={() =>
                  alert("Erro ao carregar o vídeo. Verifique a URL do stream!")
                }
              >
                <source src={selecionado.urlStream} type="application/x-mpegURL" />
                Seu navegador não suporta o formato de vídeo.
              </video>
            </div>
          ) : (
            <p>Selecione um canal para assistir.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
