import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";

const App = () => {
  const [url, setUrl] = useState(""); // URL para carregar lista M3U
  const [canais, setCanais] = useState([]); // Lista de canais
  const [selecionado, setSelecionado] = useState(null); // Canal selecionado
  const [loading, setLoading] = useState(false); // Controle de carregamento
  const [manualChannels, setManualChannels] = useState([]); // Canais adicionados manualmente
  const [newChannelName, setNewChannelName] = useState("");
  const [newChannelUrl, setNewChannelUrl] = useState("");
  const videoRef = useRef(null);

  // Função para carregar lista M3U
  const carregarListaM3U = async () => {
    setLoading(true);
    try {
      const { data } = await axios.get(url);
      const linhas = data.split("\n");
      const lista = [];

      for (let i = 0; i < linhas.length; i++) {
        if (linhas[i].startsWith("#EXTINF")) {
          const nome = linhas[i].split(",")[1];
          const urlStream = linhas[i + 1];
          lista.push({ nome, urlStream });
        }
      }
      setCanais(lista);
    } catch (error) {
      console.error("Erro ao carregar lista M3U:", error);
    }
    setLoading(false);
  };

  // Função para carregar o vídeo usando HLS.js
  const carregarVideo = (urlStream) => {
    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource(urlStream);
      hls.attachMedia(videoRef.current);
      hls.on(Hls.Events.MANIFEST_PARSED, function () {
        videoRef.current.play();
      });
    } else if (videoRef.current.canPlayType("application/vnd.apple.mpegurl")) {
      videoRef.current.src = urlStream;
      videoRef.current.addEventListener("loadedmetadata", function () {
        videoRef.current.play();
      });
    }
  };

  // Adicionar canal manualmente
  const adicionarCanal = () => {
    if (newChannelName && newChannelUrl) {
      setManualChannels([...manualChannels, { nome: newChannelName, urlStream: newChannelUrl }]);
      setNewChannelName("");
      setNewChannelUrl("");
    }
  };

  // Quando um canal é selecionado, carregar o vídeo
  useEffect(() => {
    if (selecionado) {
      carregarVideo(selecionado.urlStream);
    }
  }, [selecionado]);

  return (
    <div style={{ padding: "20px" }}>
      <h1>IPTV Web Player</h1>

      {/* Input para URL da lista M3U */}
      <input
        type="text"
        placeholder="Cole a URL da lista M3U"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={{ width: "300px", padding: "10px", marginRight: "10px" }}
      />
      <button onClick={carregarListaM3U}>Carregar Lista</button>

      <h3>Adicionar Canal Manualmente</h3>
      <input
        type="text"
        placeholder="Nome do Canal"
        value={newChannelName}
        onChange={(e) => setNewChannelName(e.target.value)}
        style={{ width: "200px", marginRight: "10px" }}
      />
      <input
        type="text"
        placeholder="URL do Stream"
        value={newChannelUrl}
        onChange={(e) => setNewChannelUrl(e.target.value)}
        style={{ width: "300px", marginRight: "10px" }}
      />
      <button onClick={adicionarCanal}>Adicionar</button>

      {/* Exibir lista de canais */}
      <div style={{ display: "flex", marginTop: "20px" }}>
        <div style={{ width: "30%", borderRight: "1px solid #ddd" }}>
          <h3>Lista de Canais</h3>
          <ul>
            {[...manualChannels, ...canais].map((canal, index) => (
              <li key={index}>
                <button onClick={() => setSelecionado(canal)}>{canal.nome}</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Exibir o player de vídeo */}
        <div style={{ width: "70%", padding: "20px" }}>
          {loading ? (
            <p>Carregando lista...</p>
          ) : selecionado ? (
            <div>
              <h3>{selecionado.nome}</h3>
              <video
                ref={videoRef}
                controls
                style={{ width: "100%", height: "500px" }}
                onError={() => alert("Erro ao carregar o vídeo. Verifique a URL do stream!")}
              >
                Seu navegador não suporta o formato de vídeo.
              </video>
            </div>
          ) : (
            <p>Selecione um canal para assistir.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
