import React, { useState, useRef } from "react";
import axios from "axios";

const App = () => {
  const [modo, setModo] = useState("painel"); // Alternar entre "painel", "m3u", "link"
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [urlServidor, setUrlServidor] = useState("");
  const [m3uLink, setM3uLink] = useState("");
  const [canais, setCanais] = useState({});
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progresso, setProgresso] = useState(0);
  const videoRef = useRef(null);

  const processarM3U = (data) => {
    const linhas = data.split("\n");
    const lista = [];
    let categoriaAtual = "Outros";

    for (let i = 0; i < linhas.length; i++) {
      if (linhas[i].startsWith("#EXTINF")) {
        const nome = linhas[i].split(",")[1];
        const urlStream = linhas[i + 1];
        if (linhas[i].includes("group-title")) {
          const match = linhas[i].match(/group-title="([^"]+)"/);
          if (match) categoriaAtual = match[1];
        }
        lista.push({ nome, urlStream, categoria: categoriaAtual });
      }
      setProgresso(Math.round((i / linhas.length) * 100));
    }

    const canaisPorCategoria = lista.reduce((acc, canal) => {
      if (!acc[canal.categoria]) acc[canal.categoria] = [];
      acc[canal.categoria].push(canal);
      return acc;
    }, {});

    setCanais(canaisPorCategoria);
    setLoading(false);
    setProgresso(100);
  };

  const carregarCanais = async () => {
    setLoading(true);
    setProgresso(0);
    try {
      const urlM3U = `${urlServidor}/get.php?username=${login}&password=${senha}&type=m3u_plus&output=mpegts`;
      const { data } = await axios.get(urlM3U);
      processarM3U(data);
    } catch (error) {
      console.error("Erro ao carregar canais:", error);
      setLoading(false);
    }
  };

  const carregarArquivo = (e) => {
    const arquivo = e.target.files[0];
    if (arquivo) {
      setLoading(true);
      setProgresso(0);
      const leitor = new FileReader();
      leitor.onload = (event) => processarM3U(event.target.result);
      leitor.readAsText(arquivo);
    }
  };

  const carregarLinkM3U = async () => {
    setLoading(true);
    setProgresso(0);
    try {
      const { data } = await axios.get(m3uLink);
      processarM3U(data);
    } catch (error) {
      console.error("Erro ao carregar lista pelo link:", error);
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>IPTV Web Player</h1>

      <div>
        <label>
          <input type="radio" value="painel" checked={modo === "painel"} onChange={() => setModo("painel")} />
          Usar Login/Senha do Painel IPTV
        </label>
        <label style={{ marginLeft: "20px" }}>
          <input type="radio" value="m3u" checked={modo === "m3u"} onChange={() => setModo("m3u")} />
          Carregar Arquivo M3U
        </label>
        <label style={{ marginLeft: "20px" }}>
          <input type="radio" value="link" checked={modo === "link"} onChange={() => setModo("link")} />
          Carregar via Link M3U
        </label>
      </div>

      {modo === "painel" && (
        <div>
          <input type="text" placeholder="Login" value={login} onChange={(e) => setLogin(e.target.value)} />
          <input type="password" placeholder="Senha" value={senha} onChange={(e) => setSenha(e.target.value)} />
          <input type="text" placeholder="URL do Servidor IPTV" value={urlServidor} onChange={(e) => setUrlServidor(e.target.value)} />
          <button onClick={carregarCanais}>Carregar Canais</button>
        </div>
      )}

      {modo === "m3u" && <input type="file" accept=".m3u" onChange={carregarArquivo} />}

      {modo === "link" && (
        <div>
          <input type="text" placeholder="Cole o link da lista M3U" value={m3uLink} onChange={(e) => setM3uLink(e.target.value)} />
          <button onClick={carregarLinkM3U}>Carregar Lista</button>
        </div>
      )}

      {loading && (
        <div>
          <p>Carregando canais... {progresso}%</p>
          <div style={{ width: "100%", height: "10px", background: "#ddd", borderRadius: "5px" }}>
            <div style={{ width: `${progresso}%`, height: "100%", background: "#007bff" }}></div>
          </div>
        </div>
      )}

      <div>
        {Object.keys(canais).map((categoria) => (
          <div key={categoria}>
            <h4>{categoria}</h4>
            {canais[categoria].map((canal, index) => (
              <button key={index} onClick={() => setSelecionado(canal)}>{canal.nome}</button>
            ))}
          </div>
        ))}
      </div>

      {selecionado && (
        <div>
          <h3>{selecionado.nome}</h3>
          <video ref={videoRef} controls width="600">
            <source src={selecionado.urlStream} type="application/x-mpegURL" />
            Seu navegador não suporta este formato.
          </video>
        </div>
      )}
    </div>
  );
};

export default App;
