import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";

const App = () => {
  const [url, setUrl] = useState("");
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [serverUrl, setServerUrl] = useState("");
  const [canais, setCanais] = useState([]);
  const [selecionado, setSelecionado] = useState(null);
  const [pesquisa, setPesquisa] = useState("");
  const videoRef = useRef(null);

  useEffect(() => {
    if (selecionado && videoRef.current) {
      const hls = new Hls();
      hls.loadSource(selecionado.url);
      hls.attachMedia(videoRef.current);
    }
  }, [selecionado]);

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <div style={{ width: "30%", padding: "20px", overflowY: "auto" }}>
        {canais.map((canal) => (
          <div key={canal.id} onClick={() => setSelecionado(canal)}>
            {canal.nome}
          </div>
        ))}
      </div>

      <div style={{ width: "70%", padding: "20px" }}>
        {selecionado ? (
          <>
            <h3>{selecionado.nome}</h3>
            <video ref={videoRef} controls style={{ width: "100%", height: "500px" }} />
            <h4>Programação</h4>
            <ul>
              {selecionado.programacao ? (
                selecionado.programacao.map((item, idx) => (
                  <li key={idx}>{item.horario} - {item.titulo}</li>
                ))
              ) : (
                <p>Sem programação disponível.</p>
              )}
            </ul>
          </>
        ) : (
          <p>Selecione um canal para assistir.</p>
        )}
      </div>
    </div>
  );
};

export default App;
