import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [url, setUrl] = useState("");
  const [canais, setCanais] = useState([]);
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progresso, setProgresso] = useState(0);

  const carregarListaM3U = async () => {
    if (!url) return;
    setLoading(true);
    setProgresso(10);
    try {
      const { data } = await axios.get(url);
      setProgresso(50);
      const linhas = data.split("\n");
      const lista = [];

      for (let i = 0; i < linhas.length; i++) {
        if (linhas[i].startsWith("#EXTINF")) {
          const nome = linhas[i].split(",")[1];
          const urlStream = linhas[i + 1]?.trim();
          lista.push({ nome, urlStream });
        }
      }
      setProgresso(80);
      setCanais(lista);
      setProgresso(100);
      localStorage.setItem("listaM3U", JSON.stringify(lista));
    } catch (error) {
      console.error("Erro ao carregar lista M3U:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ display: "flex", padding: "20px" }}>
      <div style={{ width: "30%", overflowY: "auto", maxHeight: "80vh", borderRight: "1px solid #ddd", padding: "10px" }}>
        <h1>IPTV Web Player</h1>
        <input
          type="text"
          placeholder="Cole a URL da lista M3U"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
        />
        <button onClick={carregarListaM3U} disabled={loading}>
          {loading ? `Carregando... ${progresso}%` : "Carregar Lista"}
        </button>
        <h3 style={{ marginTop: "20px" }}>Lista de Canais</h3>
        <ul>
          {canais.map((canal, index) => (
            <li key={index}>
              <button onClick={() => setSelecionado(canal)}>{canal.nome}</button>
            </li>
          ))}
        </ul>
      </div>
      <div style={{ width: "70%", padding: "20px" }}>
        {selecionado ? (
          <video controls autoPlay style={{ width: "100%", height: "500px" }}>
            <source src={selecionado.urlStream} type="application/x-mpegURL" />
          </video>
        ) : (
          <p>Selecione um canal</p>
        )}
      </div>
    </div>
  );
};

export default App;
