import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";
import { FiSearch, FiLogIn, FiUpload } from "react-icons/fi";

const App = () => {
  // ... (código anterior)

  const salvarFavoritos = (canal) => {
    const favoritos = JSON.parse(localStorage.getItem("favoritos")) || [];
    const existe = favoritos.some((fav) => fav.urlStream === canal.urlStream);
    if (!existe) {
      favoritos.push(canal);
      localStorage.setItem("favoritos", JSON.stringify(favoritos));
      alert("Canal adicionado aos favoritos!");
    }
  };

  const favoritos = JSON.parse(localStorage.getItem("favoritos")) || [];

  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>
      <div style={{ width: "30%", padding: "20px", backgroundColor: "#1f2937", color: "#fff" }}>
        {/* ... (inputs e botões existentes) */}

        <h4>Favoritos</h4>
        {favoritos.length > 0 ? (
          <ul>
            {favoritos.map((canal, index) => (
              <li key={index}>
                <button
                  onClick={() => setSelecionado(canal)}
                  style={{ width: "100%", padding: "5px", margin: "5px 0", backgroundColor: "#4f46e5", color: "#fff", borderRadius: "5px" }}
                >
                  {canal.nome}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>Sem favoritos ainda.</p>
        )}
      </div>

      <div style={{ width: "70%", padding: "20px", backgroundColor: "#111827", color: "#fff" }}>
        {selecionado && (
          <div>
            <h3>{selecionado.nome}</h3>
            <video ref={videoRef} controls style={{ width: "100%", height: "500px", borderRadius: "10px" }} />
            <button onClick={() => salvarFavoritos(selecionado)} style={{ marginTop: "10px", padding: "10px", backgroundColor: "#22c55e", color: "#fff", borderRadius: "5px" }}>Adicionar aos Favoritos</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
