import React, { useState, useRef } from "react";
import axios from "axios";

const App = () => {
  const [url, setUrl] = useState("");
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [serverUrl, setServerUrl] = useState("");
  const [file, setFile] = useState(null);
  const [canais, setCanais] = useState([]);
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(0);
  const videoRef = useRef(null);

  // Função para carregar a lista M3U de um link
  const carregarListaM3U = async (link) => {
    try {
      setLoading(10);
      const { data } = await axios.get(link);
      setLoading(50);
      const linhas = data.split("\n");
      const lista = [];

      for (let i = 0; i < linhas.length; i++) {
        if (linhas[i].startsWith("#EXTINF")) {
          const nome = linhas[i].split(",")[1];
          const urlStream = linhas[i + 1];
          lista.push({ nome, urlStream });
        }
      }

      setCanais(lista);
      localStorage.setItem("listaM3U", JSON.stringify(lista));
      setLoading(100);
    } catch (error) {
      console.error("Erro ao carregar lista M3U:", error);
    }
  };

  // Função para carregar arquivo M3U local
  const carregarArquivo = (e) => {
    const arquivo = e.target.files[0];
    if (!arquivo) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const texto = event.target.result;
      carregarListaM3U(texto);
    };
    reader.readAsText(arquivo);
  };

  // Função para carregar lista baseada em login e senha
  const carregarListaComLogin = () => {
    if (login && senha && serverUrl) {
      const urlGerada = `${serverUrl}/get.php?username=${login}&password=${senha}&type=m3u_plus&output=mpegts`;
      carregarListaM3U(urlGerada);
    }
  };

  // Controles do player
  const playVideo = () => videoRef.current.play();
  const pauseVideo = () => videoRef.current.pause();
  const toggleFullscreen = () => {
    if (videoRef.current.requestFullscreen) {
      videoRef.current.requestFullscreen();
    }
  };
  const increaseVolume = () => {
    if (videoRef.current.volume < 1) videoRef.current.volume += 0.1;
  };
  const decreaseVolume = () => {
    if (videoRef.current.volume > 0) videoRef.current.volume -= 0.1;
  };
  const forwardVideo = () => {
    videoRef.current.currentTime += 10;
  };
  const rewindVideo = () => {
    videoRef.current.currentTime -= 10;
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Barra lateral com a lista de canais */}
      <div style={{ width: "30%", overflowY: "auto", padding: "20px", borderRight: "1px solid #ddd" }}>
        <h3>Lista de Canais</h3>
        <input
          type="text"
          placeholder="Cole a URL da lista M3U"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
        />
        <button onClick={() => carregarListaM3U(url)}>Carregar via Link</button>

        <h4>Ou carregue via Login</h4>
        <input type="text" placeholder="Login" value={login} onChange={(e) => setLogin(e.target.value)} style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
        <input type="password" placeholder="Senha" value={senha} onChange={(e) => setSenha(e.target.value)} style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
        <input type="text" placeholder="URL do Servidor" value={serverUrl} onChange={(e) => setServerUrl(e.target.value)} style={{ width: "100%", padding: "10px", marginBottom: "10px" }} />
        <button onClick={carregarListaComLogin}>Carregar via Login</button>

        <h4>Ou carregue um arquivo M3U</h4>
        <input type="file" accept=".m3u" onChange={carregarArquivo} />

        {loading > 0 && loading < 100 && <p>Carregando... {loading}%</p>}

        <ul style={{ listStyle: "none", padding: 0 }}>
          {canais.map((canal, index) => (
            <li key={index} style={{ marginBottom: "10px" }}>
              <button onClick={() => setSelecionado(canal)} style={{ width: "100%", textAlign: "left", padding: "10px", border: "1px solid #ccc", background: "#f4f4f4", cursor: "pointer" }}>
                {canal.nome}
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Área do player de vídeo */}
      <div style={{ width: "70%", padding: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h1>IPTV Web Player</h1>
        {selecionado ? (
          <>
            <video ref={videoRef} controls autoPlay style={{ width: "100%", height: "500px" }}>
              <source src={selecionado.urlStream} type="application/x-mpegURL" />
              Seu navegador não suporta a reprodução deste vídeo.
            </video>
            <div style={{ marginTop: "10px", display: "flex", gap: "10px" }}>
              <button onClick={playVideo}>▶ Play</button>
              <button onClick={pauseVideo}>⏸ Pause</button>
              <button onClick={rewindVideo}>⏪ -10s</button>
              <button onClick={forwardVideo}>⏩ +10s</button>
              <button onClick={increaseVolume}>🔊 +Vol</button>
              <button onClick={decreaseVolume}>🔉 -Vol</button>
              <button onClick={toggleFullscreen}>🖥 Tela Cheia</button>
            </div>
          </>
        ) : (
          <p>Selecione um canal</p>
        )}
      </div>
    </div>
  );
};

export default App;
