import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";
import { Button, Input, Spinner } from "@/components/ui";

const App = () => {
  const [url, setUrl] = useState("");
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [serverUrl, setServerUrl] = useState("");
  const [canais, setCanais] = useState([]);
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progresso, setProgresso] = useState(0);
  const [pesquisa, setPesquisa] = useState("");
  const videoRef = useRef(null);

  const parseM3U = (data) => {
    const linhas = data.split("\n");
    let categoriaAtual = "Desconhecido";
    const lista = [];

    linhas.forEach((linha, i) => {
      if (linha.includes("group-title")) {
        const match = linha.match(/group-title="(.*?)"/);
        if (match) categoriaAtual = match[1];
      }
      if (linha.startsWith("#EXTINF")) {
        const nome = linha.split(",")[1] || "Sem nome";
        const urlStream = linhas[i + 1];
        lista.push({ nome, urlStream, categoria: categoriaAtual });
      }
    });

    const categorias = lista.reduce((acc, canal) => {
      if (!acc[canal.categoria]) acc[canal.categoria] = [];
      acc[canal.categoria].push(canal);
      return acc;
    }, {});

    return categorias;
  };

  const carregarListaM3U = async (link) => {
    setLoading(true);
    setProgresso(0);
    try {
      const { data } = await axios.get(link, {
        onDownloadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded / progressEvent.total) * 100);
          setProgresso(percentCompleted);
        },
      });
      const categorias = parseM3U(data);
      setCanais(categorias);
      localStorage.setItem("listaM3U", JSON.stringify(categorias));
    } catch (error) {
      console.error("Erro ao carregar lista M3U:", error);
    }
    setLoading(false);
  };

  const carregarListaComLogin = () => {
    if (login && senha && serverUrl) {
      const urlGerada = `${serverUrl}/get.php?username=${login}&password=${senha}&type=m3u_plus&output=mpegts`;
      carregarListaM3U(urlGerada);
    }
  };

  const carregarVideo = (urlStream) => {
    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource(urlStream);
      hls.attachMedia(videoRef.current);
      hls.on(Hls.Events.MANIFEST_PARSED, () => videoRef.current.play());
    } else if (videoRef.current.canPlayType("application/vnd.apple.mpegurl")) {
      videoRef.current.src = urlStream;
      videoRef.current.addEventListener("loadedmetadata", () => videoRef.current.play());
    }
  };

  useEffect(() => {
    if (selecionado) carregarVideo(selecionado.urlStream);
  }, [selecionado]);

  const carregarArquivo = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const categorias = parseM3U(e.target.result);
        setCanais(categorias);
        localStorage.setItem("listaM3U", JSON.stringify(categorias));
      };
      reader.readAsText(file);
    }
  };

  const canaisFiltrados = Object.entries(canais).reduce((acc, [categoria, lista]) => {
    const filtrados = lista.filter((canal) => canal.nome.toLowerCase().includes(pesquisa.toLowerCase()));
    if (filtrados.length > 0) acc[categoria] = filtrados;
    return acc;
  }, {});

  return (
    <div className="flex h-screen font-sans bg-gray-900 text-white">
      <div className="w-1/3 p-5 border-r border-gray-700 overflow-y-auto">
        <h3 className="text-xl mb-4 font-bold">Lista de Canais</h3>
        <Input placeholder="Pesquisar canal" value={pesquisa} onChange={(e) => setPesquisa(e.target.value)} className="mb-4" />
        <Input placeholder="Cole a URL da lista M3U" value={url} onChange={(e) => setUrl(e.target.value)} className="mb-4" />
        <Button onClick={() => carregarListaM3U(url)} className="w-full mb-4 bg-blue-600">Carregar via Link</Button>

        <h4 className="text-lg font-semibold mb-2">Carregar via Login</h4>
        <Input placeholder="Login" value={login} onChange={(e) => setLogin(e.target.value)} className="mb-2" />
        <Input type="password" placeholder="Senha" value={senha} onChange={(e) => setSenha(e.target.value)} className="mb-2" />
        <Input placeholder="URL do Servidor" value={serverUrl} onChange={(e) => setServerUrl(e.target.value)} className="mb-2" />
        <Button onClick={carregarListaComLogin} className="w-full mb-4 bg-blue-600">Carregar via Login</Button>

        <h4 className="text-lg font-semibold mb-2">Carregar arquivo M3U</h4>
        <Input type="file" accept=".m3u" onChange={carregarArquivo} className="mb-4" />

        {loading && <Spinner label={`Carregando lista... ${progresso}%`} className="mb-4" />}

        {Object.entries(canaisFiltrados).map(([categoria, canaisCategoria]) => (
          <div key={categoria} className="mb-4">
            <h5 className="text-md font-semibold mb-2">{categoria}</h5>
            <ul>
              {canaisCategoria.map((canal, index) => (
                <li key={index} className="mb-2">
                  <Button onClick={() => setSelecionado(canal)} className="w-full text-left bg-gray-700 hover:bg-gray-600">
                    {canal.nome}
                  </Button>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      <div className="w-2/3 p-5 bg-gray-800">
        {loading ? (
          <Spinner label={`Carregando lista... ${progresso}%`} />
        ) : selecionado ? (
          <div>
            <h3 className="text-2xl font-bold mb-4">{selecionado.nome}</h3>
            <video ref={videoRef} controls className="w-full h-[500px] border-2 border-white rounded-lg" />
          </div>
        ) : (
          <p className="text-center text-lg">Selecione um canal para assistir.</p>
        )}
      </div>
    </div>
  );
};

export default App;
