import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";

const App = () => {
  const [url, setUrl] = useState("");
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [serverUrl, setServerUrl] = useState("");
  const [canais, setCanais] = useState([]);
  const [selecionado, setSelecionado] = useState(null);
  const [pesquisa, setPesquisa] = useState("");
  const [historico, setHistorico] = useState([]);
  const videoRef = useRef(null);

  useEffect(() => {
    if (selecionado && videoRef.current) {
      const hls = new Hls();
      hls.loadSource(selecionado.url);
      hls.attachMedia(videoRef.current);

      if (!historico.includes(selecionado.url)) {
        setHistorico((prev) => [...prev, selecionado.url]);
      }
    }
  }, [selecionado]);

  return (
    <div className="flex flex-col md:flex-row h-screen">
      <div className="w-full md:w-1/3 p-4 overflow-y-auto">
        {canais.map((canal) => (
          <div key={canal.id} onClick={() => setSelecionado(canal)} className="cursor-pointer p-2 hover:bg-gray-200">
            {canal.nome}
          </div>
        ))}
        <h4 className="mt-4">Histórico</h4>
        <ul>
          {historico.map((link, idx) => (
            <li key={idx} className="text-sm break-words">{link}</li>
          ))}
        </ul>
      </div>

      <div className="w-full md:w-2/3 p-4">
        {selecionado ? (
          <>
            <h3>{selecionado.nome}</h3>
            <video ref={videoRef} controls className="w-full h-auto" />
            <h4 className="mt-4">Programação</h4>
            <ul>
              {selecionado.programacao ? (
                selecionado.programacao.map((item, idx) => (
                  <li key={idx}>{item.horario} - {item.titulo}</li>
                ))
              ) : (
                <p>Sem programação disponível.</p>
              )}
            </ul>
          </>
        ) : (
          <p>Selecione um canal para assistir.</p>
        )}
      </div>
    </div>
  );
};

export default App;
