import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Hls from "hls.js";

const App = () => {
  const [url, setUrl] = useState("");
  const [login, setLogin] = useState("");
  const [senha, setSenha] = useState("");
  const [serverUrl, setServerUrl] = useState("");
  const [canais, setCanais] = useState({ streams: [], filmes: [], series: [] });
  const [selecionado, setSelecionado] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progresso, setProgresso] = useState(0);
  const videoRef = useRef(null);

  const categorizarCanais = (data) => {
    const linhas = data.split("\n");
    const streams = [];
    const filmes = [];
    const series = [];

    for (let i = 0; i < linhas.length; i++) {
      if (linhas[i].startsWith("#EXTINF")) {
        const nome = linhas[i].split(",")[1];
        const urlStream = linhas[i + 1];

        if (/filme|movie/i.test(nome)) {
          filmes.push({ nome, urlStream });
        } else if (/serie|series/i.test(nome)) {
          series.push({ nome, urlStream });
        } else {
          streams.push({ nome, urlStream });
        }
      }
      setProgresso(Math.min(100, Math.floor((i / linhas.length) * 100)));
    }
    return { streams, filmes, series };
  };

  const carregarListaM3U = async (link) => {
    setLoading(true);
    setProgresso(0);
    try {
      const { data } = await axios.get(link, {
        onDownloadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgresso(percentCompleted);
        }
      });
      const lista = categorizarCanais(data);
      setCanais(lista);
      localStorage.setItem("listaM3U", JSON.stringify(lista));
    } catch (error) {
      console.error("Erro ao carregar lista M3U:", error);
    }
    setLoading(false);
  };

  const carregarListaComLogin = () => {
    if (login && senha && serverUrl) {
      const urlGerada = `${serverUrl}/get.php?username=${login}&password=${senha}&type=m3u_plus&output=mpegts`;
      carregarListaM3U(urlGerada);
    }
  };

  const carregarVideo = (urlStream) => {
    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource(urlStream);
      hls.attachMedia(videoRef.current);
      hls.on(Hls.Events.MANIFEST_PARSED, () => {
        videoRef.current.play();
      });
    } else if (videoRef.current.canPlayType("application/vnd.apple.mpegurl")) {
      videoRef.current.src = urlStream;
      videoRef.current.addEventListener("loadedmetadata", () => {
        videoRef.current.play();
      });
    }
  };

  useEffect(() => {
    if (selecionado) carregarVideo(selecionado.urlStream);
  }, [selecionado]);

  const carregarArquivo = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const lista = categorizarCanais(e.target.result);
        setCanais(lista);
        localStorage.setItem("listaM3U", JSON.stringify(lista));
      };
      reader.readAsText(file);
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <div style={{ width: "30%", padding: "20px", borderRight: "1px solid #ddd" }}>
        <h3>Lista de Canais</h3>
        <input type="text" placeholder="Cole a URL da lista M3U" value={url} onChange={(e) => setUrl(e.target.value)} />
        <button onClick={() => carregarListaM3U(url)}>Carregar via Link</button>

        <h4>Ou carregue via Login</h4>
        <input type="text" placeholder="Login" value={login} onChange={(e) => setLogin(e.target.value)} />
        <input type="password" placeholder="Senha" value={senha} onChange={(e) => setSenha(e.target.value)} />
        <input type="text" placeholder="URL do Servidor" value={serverUrl} onChange={(e) => setServerUrl(e.target.value)} />
        <button onClick={carregarListaComLogin}>Carregar via Login</button>

        <h4>Ou carregue um arquivo M3U</h4>
        <input type="file" accept=".m3u" onChange={carregarArquivo} />

        {loading && <p>Carregando... {progresso}%</p>}

        <h5>Streams:</h5>
        <ul>
          {canais.streams.map((canal, index) => (
            <li key={index}>
              <button onClick={() => setSelecionado(canal)}>{canal.nome}</button>
            </li>
          ))}
        </ul>

        <h5>Filmes:</h5>
        <ul>
          {canais.filmes.map((canal, index) => (
            <li key={index}>
              <button onClick={() => setSelecionado(canal)}>{canal.nome}</button>
            </li>
          ))}
        </ul>

        <h5>Séries:</h5>
        <ul>
          {canais.series.map((canal, index) => (
            <li key={index}>
              <button onClick={() => setSelecionado(canal)}>{canal.nome}</button>
            </li>
          ))}
        </ul>
      </div>
      <div style={{ width: "70%", padding: "20px" }}>
        {loading ? (
          <p>Carregando lista... {progresso}%</p>
        ) : selecionado ? (
          <div>
            <h3>{selecionado.nome}</h3>
            <video ref={videoRef} controls style={{ width: "100%", height: "500px" }} />
          </div>
        ) : (
          <p>Selecione um canal para assistir.</p>
        )}
      </div>
    </div>
  );
};

export default App;
