import React, { useState, useRef } from "react";
import axios from "axios";

const App = () => {
  const [url, setUrl] = useState(""); // URL da lista M3U
  const [canais, setCanais] = useState({}); // Canais organizados por categoria
  const [selecionado, setSelecionado] = useState(null); // Canal selecionado
  const [loading, setLoading] = useState(false); // Controle de loading
  const videoRef = useRef(null); // Referência ao player de vídeo
  const scrollRef = useRef(null); // Referência para o scroll da lista de canais

  // Função para carregar e processar a lista M3U
  const carregarListaM3U = async () => {
    setLoading(true); // Iniciar carregamento
    try {
      const { data } = await axios.get(url);
      const linhas = data.split("\n");
      const lista = [];
      let categoriaAtual = "";

      // Processando a lista M3U
      for (let i = 0; i < linhas.length; i++) {
        if (linhas[i].startsWith("#EXTINF")) {
          const nome = linhas[i].split(",")[1];
          const urlStream = linhas[i + 1];
          const categoria = linhas[i].includes("#EXTGRP") ? linhas[i].split("#EXTGRP:")[1].trim() : "Outro"; // Categorias
          lista.push({ nome, urlStream, categoria });
        }
      }

      // Agrupando os canais por categoria
      const canaisPorCategoria = lista.reduce((acc, canal) => {
        if (!acc[canal.categoria]) acc[canal.categoria] = [];
        acc[canal.categoria].push(canal);
        return acc;
      }, {});

      setCanais(canaisPorCategoria); // Atualizar canais organizados por categoria
    } catch (error) {
      console.error("Erro ao carregar lista M3U:", error);
    }
    setLoading(false); // Finalizar carregamento
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>IPTV Web Player</h1>

      {/* Campo para inserir URL da lista M3U */}
      <input
        type="text"
        placeholder="Cole a URL da lista M3U"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={{ width: "300px", padding: "10px", marginRight: "10px" }}
      />
      <button onClick={carregarListaM3U}>Carregar Lista</button>

      <div style={{ display: "flex", marginTop: "20px" }}>
        <div
          style={{
            width: "30%",
            borderRight: "1px solid #ddd",
            overflowY: "scroll",
            maxHeight: "600px",
          }}
          ref={scrollRef}
        >
          <h3>Lista de Canais</h3>

          {/* Exibir Categorias e canais */}
          {Object.keys(canais).map((categoria) => (
            <div key={categoria}>
              <h4>{categoria}</h4>
              <ul>
                {canais[categoria].map((canal, index) => (
                  <li key={index}>
                    <button onClick={() => setSelecionado(canal)}>
                      {canal.nome}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>

        <div style={{ width: "70%", padding: "20px", overflow: "hidden" }}>
          {loading ? (
            <p>Carregando lista...</p>
          ) : selecionado ? (
            <div>
              <h3>{selecionado.nome}</h3>
              <video
                ref={videoRef}
                controls
                style={{ width: "100%", height: "500px" }}
                onError={() => alert("Erro ao carregar o vídeo. Verifique a URL do stream!")}
              >
                <source src={selecionado.urlStream} type="application/x-mpegURL" />
                Seu navegador não suporta o formato de vídeo.
              </video>
            </div>
          ) : (
            <p>Selecione um canal para assistir.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
